import React from "react";
import "./sideSelectionOverlay.css";

export default function SideSelectionOverlay({ teamLogo, teamName, side }) {
  const isBlue = side === "blue";
  return (
    <div className={`side-selection-overlay ${isBlue ? "blue" : "red"}`}>
      <img src={teamLogo} alt={teamName} className="side-logo" />
      <div className="side-info">
        <span className="side-team">{teamName}</span>
        <span className="side-label">{isBlue ? "BLUE" : "RED"}</span>
      </div>
    </div>
  );
}
