import React from 'react';
import { useParams } from 'react-router-dom';
import styles from './TeamLineupOverlay.module.css';

// Import lane icons
import topIcon from './assets/lane-logos/top.png';
import jungleIcon from './assets/lane-logos/jungle.png';
import middleIcon from './assets/lane-logos/middle.png';
import bottomIcon from './assets/lane-logos/bottom.png';
import supportIcon from './assets/lane-logos/support.png';

// Import team logos
import team2Logo from './assets/team-2-logo-only.png';
import team3Logo from './assets/team-3-logo-only.png';

// Import team 2 player images
import team2Top from './assets/team-2-players/top.jpeg';
import team2Jungle from './assets/team-2-players/jg.jpeg';
import team2Mid from './assets/team-2-players/mid.jpeg';
import team2Adc from './assets/team-2-players/adc.jpeg';
import team2Support from './assets/team-2-players/supp.jpeg';

// Import team 3 player images
import team3Top from './assets/team-3-players/top.jpeg';
import team3Jungle from './assets/team-3-players/jg.jpeg';
import team3Mid from './assets/team-3-players/mid.jpeg';
import team3Adc from './assets/team-3-players/adc.jpeg';
import team3Support from './assets/team-3-players/supp.jpeg';

const roleIcons = {
  TOP: topIcon,
  JNG: jungleIcon,
  MID: middleIcon,
  ADC: bottomIcon,
  SUP: supportIcon,
};

const TeamLineupOverlay = () => {
  const { team } = useParams();

  // Data for both teams - please update player names as needed
  const teamsData = {
    // Team 2 (afk) - only participated in Round 1 and 2, then went to finals
    afk: {
      championship: "DOWNTOWN CAMPUS",
      location: "RAZER STORE SEATTLE",
      stage: "FINALS",
      teamName: "AFK",
      region: "ANOINTED FOR KINGDOM",
      logo: team2Logo,
      players: [
        {
          name: "Player1", // Replace with actual top player name
          role: "TOP",
          image: team2Top
        },
        {
          name: "Player2", // Replace with actual jungle player name
          role: "JNG",
          image: team2Jungle
        },
        {
          name: "Player3", // Replace with actual mid player name
          role: "MID", 
          image: team2Mid
        },
        {
          name: "Player4", // Replace with actual adc player name
          role: "ADC",
          image: team2Adc
        },
        {
          name: "Player5", // Replace with actual support player name
          role: "SUP",
          image: team2Support
        }
      ],
      matchHistory: [
        { stage: "ROUND 1", round: "", opponent: "TEAM A", result: "W", score: "2:0" },
        { stage: "ROUND 2", round: "", opponent: "TEAM B", result: "W", score: "2:1" },
        { stage: "FINALS", round: "", opponent: "TBD", result: "TBD", score: "" }
      ]
    },
    // Team 3 (uw3) - participated in all rounds
    uw3: {
      championship: "DOWNTOWN CAMPUS",
      location: "RAZER STORE SEATTLE", 
      stage: "FINALS",
      teamName: "UW3",
      region: "ESPORTS",
      logo: team3Logo,
      players: [
        {
          name: "Player6", // Replace with actual top player name
          role: "TOP",
          image: team3Top
        },
        {
          name: "Player7", // Replace with actual jungle player name
          role: "JNG",
          image: team3Jungle
        },
        {
          name: "Player8", // Replace with actual mid player name
          role: "MID", 
          image: team3Mid
        },
        {
          name: "Player9", // Replace with actual adc player name
          role: "ADC",
          image: team3Adc
        },
        {
          name: "Player10", // Replace with actual support player name
          role: "SUP",
          image: team3Support
        }
      ],
      matchHistory: [
        { stage: "ROUND 1", round: "", opponent: "TEAM C", result: "W", score: "2:0" },
        { stage: "ROUND 2", round: "", opponent: "TEAM D", result: "W", score: "2:1" },
        { stage: "ROUND 3", round: "", opponent: "TEAM E", result: "W", score: "2:0" },
        { stage: "FINALS", round: "", opponent: "TBD", result: "TBD", score: "" }
      ]
    }
  };

  // Get team data based on parameter, default to afk if no param or invalid param
  const teamData = teamsData[team] || teamsData.afk;

  return (
    <div className={styles.overlay}>
      {/* Background and frame */}
      <div className={styles.background}>
        {/* Header section */}
        <div className={styles.header}>
          <div className={styles.championship}>
            <div className={styles.championshipText}>{teamData.championship}</div>
            <div className={styles.locationText}>{teamData.location}</div>
            <div className={styles.stageText}>{teamData.stage}</div>
          </div>

          <div className={styles.teamInfo}>
            <div className={styles.teamLogo}>
              <img src={teamData.logo} alt={teamData.teamName} />
            </div>
            <div className={styles.teamName}>{teamData.teamName}</div>
            <div className={styles.teamRegion}>{teamData.region}</div>
          </div>

          <div className={styles.leagueLogo}>
            <div className={styles.leagueText}>LEAGUE<span className={styles.ofText}>OF</span></div>
            <div className={styles.legendsText}>LEGENDS</div>
          </div>
        </div>

        {/* Players container with outer position icons */}
        <div className={styles.playersContainer}>
          {/* Position icons positioned outside the player lineup */}
          <div className={styles.topIcon}>
            <img src={roleIcons.TOP} alt="TOP" />
          </div>
          <div className={styles.jungleIcon}>
            <img src={roleIcons.JNG} alt="JNG" />
          </div>
          <div className={styles.midIcon}>
            <img src={roleIcons.MID} alt="MID" />
          </div>
          <div className={styles.adcIcon}>
            <img src={roleIcons.ADC} alt="ADC" />
          </div>
          <div className={styles.supportIcon}>
            <img src={roleIcons.SUP} alt="SUP" />
          </div>

          {/* Player lineup section */}
          <div className={styles.players}>
            {teamData.players.map((player, index) => (
              <div key={index} className={styles.playerCard}>
                <div className={styles.playerName}>{player.name}</div>
                <div className={styles.playerImage}>
                  <img src={player.image} alt={player.name} />
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Tournament progression section */}
        <div className={styles.tournament}>
          {teamData.matchHistory.map((match, index) => (
            <div key={index} className={styles.matchInfo}>
              <div className={styles.stageName}>{match.stage}</div>
              <div className={styles.matchDetails}>
                {match.round && <span className={styles.round}>{match.round}</span>}
                <span className={styles.vsText}>VS {match.opponent}</span>
                {match.score && <span className={styles.score}>{match.score}</span>}
              </div>
            </div>
          ))}
        </div>

        {/* Decorative frame elements */}
        <div className={styles.frameTopLeft}></div>
        <div className={styles.frameTopRight}></div>
        <div className={styles.frameBottomLeft}></div>
        <div className={styles.frameBottomRight}></div>
        <div className={styles.gridLines}></div>
      </div>
    </div>
  );
};

export default TeamLineupOverlay;
